package com.xworks.Inheritance.external;

public class Warrior {
    public void warrior() {
        System.out.println("No-args constructor of Warrior");
    }

    public void fighting() {
        System.out.println("Warrior is fighting");
    }

    public void defending() {
        System.out.println("Warrior is defending");
    }

    public void riding() {
        System.out.println("Warrior is riding");
    }

    public void training() {
        System.out.println("Warrior is training");
    }

    public void strategizing() {
        System.out.println("Warrior is strategizing");
    }
}
