package com.xworks.Inheritance.external;

public class Weapon {
    public void load() {
        System.out.println("Weapon is loading...");
    }

    public void shoot() {
        System.out.println("Weapon is shooting...");
    }

    public void reload() {
        System.out.println("Weapon is reloading...");
    }

    public void aim() {
        System.out.println("Weapon is aiming...");
    }

    public void safetyLock() {
        System.out.println("Weapon safety lock engaged...");
    }
}
