package com.xworks.Inheritance.external;

public class Window {
    public void open() {
        System.out.println("Window is opened");
    }

    public void close() {
        System.out.println("Window is closed");
    }

    public void lock() {
        System.out.println("Window is locked");
    }

    public void clean() {
        System.out.println("Window is being cleaned");
    }

    public void resize() {
        System.out.println("Window size adjusted");
    }
}
