package com.xworks.Inheritance.external;

public class Write {
    public void write() {
        System.out.println("Writer is writing...");
    }

    public void edit() {
        System.out.println("Writer is editing...");
    }

    public void publish() {
        System.out.println("Writer is publishing...");
    }

    public void research() {
        System.out.println("Writer is researching...");
    }

    public void read() {
        System.out.println("Writer is reading...");
    }
}
